insert  into `rp_graficos`(`id`,`nombre`,`tipo_grafico`,`slug`,`descipcion`,`activo`,`created`,`modified`) values (10,'Marcas del periodo',1,'marcas_del_periodo','SELECT\r\n     Producto.reference AS Referencia,\r\n     Proveedor.name AS Nombre,\r\n     SUM(CarroProducto.quantity) AS Cantidad\r\nFROM [*PREFIX*]cart AS Carro \r\nINNER JOIN\r\n      [*PREFIX*]orders AS Orden\r\n      ON \r\n      Orden.id_cart = Carro.id_cart\r\n      AND\r\n      Orden.date_add BETWEEN [*START_DATE*] AND [*FINISH_DATE*] \r\n      AND\r\n      ( Orden.current_state = \'2\' \r\n      Or Orden.current_state = \'3\' \r\n      Or Orden.current_state = \'4\'\r\n      Or Orden.current_state = \'5\' )\r\nINNER JOIN \r\n  [*PREFIX*]cart_product AS CarroProducto\r\n  ON CarroProducto.id_cart = Carro.id_cart\r\nINNER JOIN\r\n  [*PREFIX*]product AS Producto\r\n  ON Producto.id_product = CarroProducto.id_product\r\nINNER JOIN [*PREFIX*]product_supplier AS ProductoProveedor\r\n  ON ProductoProveedor.id_product = Producto.id_product\r\nINNER JOIN [*PREFIX*]supplier AS Proveedor\r\n  ON Proveedor.id_supplier = ProductoProveedor.id_supplier\r\nGROUP BY Proveedor.id_supplier\r\nORDER BY Cantidad DESC\r\nLIMIT 20',1,'2017-01-20 13:43:02','2017-02-22 11:37:29'),(9,'Compradores del periodo',4,'compradores_del_periodo','Select \r\n       ROUND(SUM(Orden.total_paid_real / 1.19)) As pagado, \r\n       CONCAT(Cliente.firstname, \' \', Cliente.lastname) AS nombre   \r\nFrom \r\n       [*PREFIX*]customer AS Cliente\r\nLeft join [*PREFIX*]orders AS Orden\r\n     On Orden.id_customer = Cliente.id_customer \r\nWhere ( Orden.current_state = \'2\' \r\n     Or Orden.current_state = \'3\' \r\n     Or Orden.current_state = \'4\'\r\n     Or Orden.current_state = \'5\' )\r\n     AND Orden.date_add >= [*START_DATE*]  \r\n     AND Orden.date_add <= DATE_ADD([*FINISH_DATE*], INTERVAL 86399 SECOND )\r\ngroup by Cliente.id_customer\r\nOrder by ROUND(SUM(Orden.total_paid_real)) DESC\r\nLimit 10;',1,'2017-01-19 16:59:16','2017-01-23 16:03:36'),(8,'Pedidos del periodo',5,'pedidos_del_periodo','Select \r\n       Count(Orden.id_order) As Total,\r\n       MONTHNAME(Orden.date_add) AS Mes,\r\n[*GROUP_BY_COL*]\r\nFrom [*PREFIX*]orders AS Orden \r\nWhere \r\n      Orden.date_add BETWEEN [*START_DATE*] AND [*FINISH_DATE*] \r\n      AND (Orden.current_state = \'2\' \r\nOR Orden.current_state = \'3\'  \r\nOR Orden.current_state = \'4\' \r\nOR Orden.current_state = \'5\' \r\nOR Orden.current_state = \'18\'\r\nOR Orden.current_state = \'25\'\r\nOR Orden.current_state = \'26\'  )\r\n[*GROUP_BY*]',1,'2017-01-19 16:29:01','2017-03-03 10:33:29'),(12,'Top marcas',4,'top_marcas','SELECT\r\n     Fabricante.name AS Nombre,\r\n     SUM(DetalleOrden.product_quantity) AS Cantidad\r\nFROM [*PREFIX*]orders AS Orden\r\nINNER JOIN [*PREFIX*]order_detail AS DetalleOrden\r\n	ON DetalleOrden.`id_order` = Orden.`id_order`\r\nINNER JOIN [*PREFIX*]product AS Producto\r\n      ON Producto.id_product = DetalleOrden.product_id\r\nINNER JOIN [*PREFIX*]manufacturer AS Fabricante\r\n  ON Fabricante.id_manufacturer = Producto.id_manufacturer\r\nWHERE \r\n      Orden.date_add BETWEEN [*START_DATE*] AND [*FINISH_DATE*]\r\n      AND (\r\n      Orden.current_state = \'2\' \r\nOR Orden.current_state = \'3\'  \r\nOR Orden.current_state = \'4\' \r\nOR Orden.current_state = \'5\' \r\nOR Orden.current_state = \'18\'\r\nOR Orden.current_state = \'25\'\r\nOR Orden.current_state = \'26\'   )\r\nGROUP BY Producto.id_manufacturer\r\nORDER BY Cantidad DESC',1,'2017-02-22 10:59:32','2017-04-05 17:59:12'),(15,'Total despachos',6,'total_despachos','SELECT\r\n	ROUND(SUM(Orden.`total_shipping`)) AS Envio\r\nFROM [*PREFIX*]orders AS Orden\r\nWHERE \r\n      Orden.date_add BETWEEN [*START_DATE*] AND [*FINISH_DATE*]\r\n      AND (\r\n      Orden.current_state = \'2\' \r\nOR Orden.current_state = \'3\'  \r\nOR Orden.current_state = \'4\' \r\nOR Orden.current_state = \'5\' \r\nOR Orden.current_state = \'18\'\r\nOR Orden.current_state = \'25\'\r\nOR Orden.current_state = \'26\'   )',1,'2017-04-06 10:38:26','2017-04-06 10:38:26'),(7,'Total descuentos del periodo',5,'total_descuentos_del_periodo','Select \r\n       ROUND(Sum(Orden.total_discounts_tax_incl)) As Total, \r\n       [*GROUP_BY_COL*]\r\nFrom \r\n       [*PREFIX*]orders AS Orden \r\nWhere ( Orden.current_state = \'2\' \r\nOR Orden.current_state = \'3\'  \r\nOR Orden.current_state = \'4\' \r\nOR Orden.current_state = \'5\' \r\nOR Orden.current_state = \'18\'\r\nOR Orden.current_state = \'25\'\r\nOR Orden.current_state = \'26\'   )\r\n      AND Orden.date_add BETWEEN [*START_DATE*] AND [*FINISH_DATE*] \r\n[*GROUP_BY*]',1,'2017-01-19 16:24:57','2017-03-03 10:35:05'),(11,'Top productos',4,'top_productos','SELECT\r\n     Producto.reference AS Referencia,\r\n     SUM(DetalleOrden.product_quantity) AS Cantidad\r\nFROM [*PREFIX*]orders AS Orden\r\nINNER JOIN [*PREFIX*]order_detail AS DetalleOrden\r\n	ON DetalleOrden.`id_order` = Orden.`id_order`\r\nINNER JOIN [*PREFIX*]product AS Producto\r\n      ON Producto.id_product = DetalleOrden.product_id\r\nWHERE \r\n      Orden.date_add BETWEEN [*START_DATE*] AND [*FINISH_DATE*]\r\n      AND (\r\n      Orden.current_state = \'2\' \r\nOR Orden.current_state = \'3\'  \r\nOR Orden.current_state = \'4\' \r\nOR Orden.current_state = \'5\' \r\nOR Orden.current_state = \'18\'\r\nOR Orden.current_state = \'25\'\r\nOR Orden.current_state = \'26\'   )\r\nGROUP BY DetalleOrden.product_id\r\nORDER BY Cantidad DESC\r\n[*LIMIT*]',1,'2017-02-21 16:42:36','2017-04-05 17:57:08'),(6,'Productos del periodo',2,'productos_del_periodo','SELECT\r\n     Producto.reference AS Referencia,\r\n     SUM(CarroProducto.quantity) AS Cantidad\r\nFROM [*PREFIX*]cart AS Carro\r\nINNER JOIN\r\n      [*PREFIX*]orders AS Orden\r\n      ON \r\n      Orden.id_cart = Carro.id_cart\r\n      AND\r\n      Orden.date_add >= [*START_DATE*]\r\n      AND\r\n      Orden.date_add <= DATE_ADD([*FINISH_DATE*], INTERVAL 86399 SECOND )\r\n      AND\r\n      ( Orden.current_state = \'2\' \r\n      Or Orden.current_state = \'3\' \r\n      Or Orden.current_state = \'4\'\r\n      Or Orden.current_state = \'5\' )\r\nINNER JOIN \r\n  [*PREFIX*]cart_product AS CarroProducto\r\n  ON CarroProducto.id_cart = Carro.id_cart\r\nINNER JOIN\r\n  [*PREFIX*]product AS Producto\r\n  ON Producto.id_product = CarroProducto.id_product\r\n        AND Producto.active = 1\r\nGROUP BY Producto.id_product\r\nORDER BY Cantidad DESC',1,'2017-01-18 16:06:07','2017-01-23 16:02:41'),(5,'Comparador de periodos',2,'comparador_de_periodos','Select ROUND(SUM(Orden.total_paid_real / 1.19)) As VentaPeriodoActual, \r\n       Concat(\'$\', Format(ROUND(SUM(Orden.total_paid_real)), 0, \'de_DE\')) As Monto,\r\n       ROUND(Sum(Orden.total_discounts_tax_incl / 1.19)) As TotalDescuentos,\r\n       DATE_FORMAT(Orden.date_add, \'%Y-%m\') AS Mes,\r\n       DATE_ADD([*START_DATE*], INTERVAL -1 MONTH) AS PeriodoAnteriorInicio,\r\n       DATE_ADD(DATE_ADD([*FINISH_DATE*], INTERVAL 86399 SECOND ), INTERVAL -1 MONTH) AS PeriodoAnteriorFinal  \r\nFrom [*PREFIX*]orders as Orden\r\nWhere (  \r\n      Orden.date_add >= DATE_ADD([*START_DATE*], INTERVAL -1 MONTH)\r\n      And Orden.date_add <= DATE_ADD(DATE_ADD([*FINISH_DATE*], INTERVAL 86399 SECOND ), INTERVAL -1 MONTH)\r\n      AND (Orden.current_state = \'2\' \r\n      Or Orden.current_state = \'3\' \r\n      Or Orden.current_state = \'4\'\r\n      Or Orden.current_state = \'5\')\r\n)\r\nOR (\r\n   Orden.date_add <= DATE_ADD([*FINISH_DATE*], INTERVAL 86399 SECOND )  \r\n   And Orden.date_add >= [*START_DATE*]\r\n   AND (Orden.current_state = \'2\' \r\n      Or Orden.current_state = \'3\' \r\n      Or Orden.current_state = \'4\'\r\n      Or Orden.current_state = \'5\')\r\n)\r\nGROUP BY MONTH(Orden.date_add)\r\nORDER BY Orden.date_add ASC',1,'2017-01-18 15:46:12','2017-01-25 14:00:01'),(1,'Total ventas del periodo',5,'total_ventas_del_periodo','Select \r\nROUND(SUM(Orden.total_paid_real)) As Total, \r\n[*GROUP_BY_COL*]\r\nFROM [*PREFIX*]orders AS Orden\r\nWHERE\r\nOrden.date_add BETWEEN [*START_DATE*] AND [*FINISH_DATE*] \r\nAND (\r\nOrden.current_state = \'2\' \r\nOR Orden.current_state = \'3\'  \r\nOR Orden.current_state = \'4\' \r\nOR Orden.current_state = \'5\' \r\nOR Orden.current_state = \'18\'\r\nOR Orden.current_state = \'25\'\r\nOR Orden.current_state = \'26\'   )\r\n[*GROUP_BY*]',1,'2017-01-17 12:57:20','2017-03-03 10:32:30'),(4,'Categorías del periodo',4,'categorias_del_periodo','SELECT\r\n     IdiomaCategoria.name AS Nombre,\r\n     SUM(CarroProducto.quantity) AS Cantidad,\r\n     CategoriaProducto.id_category AS Id\r\nFROM [*PREFIX*]cart AS Carro\r\nINNER JOIN\r\n      [*PREFIX*]orders AS Orden\r\n      ON \r\n      Orden.id_cart = Carro.id_cart\r\n      AND\r\n      Orden.date_add >= [*START_DATE*]\r\n      AND\r\n      Orden.date_add <= [*FINISH_DATE*]\r\n      AND\r\n      ( Orden.current_state = \'2\' \r\n      Or Orden.current_state = \'3\' \r\n      Or Orden.current_state = \'4\'\r\n      Or Orden.current_state = \'5\' )\r\nINNER JOIN \r\n	[*PREFIX*]cart_product AS CarroProducto\r\n	ON CarroProducto.id_cart = Carro.id_cart\r\nINNER JOIN\r\n	[*PREFIX*]product AS Producto\r\n	ON Producto.id_product = CarroProducto.id_product\r\nINNER JOIN [*PREFIX*]category_product AS CategoriaProducto\r\n	ON CategoriaProducto.id_product = Producto.id_product\r\nINNER JOIN [*PREFIX*]category AS Categoria\r\n	ON Categoria.id_category = CategoriaProducto.id_category\r\nLEFT JOIN\r\n      [*PREFIX*]category_lang AS IdiomaCategoria\r\n      ON IdiomaCategoria.id_category = Categoria.id_category\r\n\r\nGROUP BY Categoria.id_category\r\nORDER BY Cantidad DESC',1,'2017-01-18 10:09:43','2017-01-23 16:21:27'),(13,'Top clientes',4,'top_clientes','Select \r\n       ROUND(SUM(Orden.total_paid_real)) As pagado, \r\n       CONCAT(Cliente.firstname, \' \', Cliente.lastname) AS nombre   \r\nFrom \r\n       [*PREFIX*]customer AS Cliente\r\nLeft join [*PREFIX*]orders AS Orden\r\n     On Orden.id_customer = Cliente.id_customer \r\nWhere ( Orden.current_state = \'2\' \r\nOR Orden.current_state = \'3\'  \r\nOR Orden.current_state = \'4\' \r\nOR Orden.current_state = \'5\' \r\nOR Orden.current_state = \'18\'\r\nOR Orden.current_state = \'25\'\r\nOR Orden.current_state = \'26\'   )\r\n     AND Orden.date_add BETWEEN [*START_DATE*]  AND [*FINISH_DATE*]\r\ngroup by Cliente.id_customer\r\nOrder by ROUND(SUM(Orden.total_paid_real)) DESC\r\nLimit 10;',1,'2017-02-22 15:48:00','2017-03-03 10:36:52'),(14,'Tabla Marcas',6,'tabla_marcas','SELECT \r\n  Fabricante.name AS Marca,\r\n  ROUND(ROUND(SUM(ROUND(DetalleOrden.total_price_tax_incl)) * 100) / (SELECT \r\n  SUM(ROUND(DetalleOrden.total_price_tax_incl))\r\nFROM [*PREFIX*]orders AS Orden\r\nINNER JOIN [*PREFIX*]cart AS Carro\r\n      ON Carro.id_cart = Orden.id_cart\r\nINNER JOIN [*PREFIX*]cart_product AS CarroProducto\r\n      ON CarroProducto.id_cart = Carro.id_cart\r\nINNER JOIN [*PREFIX*]product AS Producto\r\n      ON Producto.id_product = CarroProducto.id_product\r\nINNER JOIN [*PREFIX*]product_lang AS ProductoLang\r\n      ON ProductoLang.id_product = Producto.id_product\r\nINNER JOIN [*PREFIX*]manufacturer AS Fabricante\r\n  ON Fabricante.id_manufacturer = Producto.id_manufacturer\r\nINNER JOIN [*PREFIX*]order_detail AS DetalleOrden\r\n      ON DetalleOrden.`product_id` = Producto.`id_product`\r\n      AND DetalleOrden.`id_order` = Orden.`id_order`\r\nWHERE \r\n      Orden.date_add BETWEEN [*START_DATE*] AND [*FINISH_DATE*]\r\n      AND (\r\n       Orden.current_state = \'2\' \r\n  OR Orden.current_state = \'3\'  \r\n  OR Orden.current_state = \'4\' \r\n  OR Orden.current_state = \'5\' \r\n  OR Orden.current_state = \'18\'\r\n  OR Orden.current_state = \'25\'\r\n  OR Orden.current_state = \'26\' )))  AS Total,\r\n  SUM(DetalleOrden.product_quantity) AS Cantidad,\r\n        SUM(ROUND(DetalleOrden.total_price_tax_incl)) AS PrecioVenta,\r\n  (SELECT\r\n  ROUND(SUM(Orden.`total_shipping`)) AS Envio\r\nFROM [*PREFIX*]orders AS Orden\r\nWHERE \r\n      Orden.date_add BETWEEN [*START_DATE*] AND [*FINISH_DATE*]\r\n      AND (\r\n      Orden.current_state = \'2\' \r\nOR Orden.current_state = \'3\'  \r\nOR Orden.current_state = \'4\' \r\nOR Orden.current_state = \'5\' \r\nOR Orden.current_state = \'18\'\r\nOR Orden.current_state = \'25\'\r\nOR Orden.current_state = \'26\'   )) AS Despachos,\r\n(SELECT \r\n       ROUND(SUM(Orden.total_discounts_tax_incl)) AS Total\r\nFROM \r\n       [*PREFIX*]orders AS Orden \r\nWHERE ( Orden.current_state = \'2\' \r\nOR Orden.current_state = \'3\'  \r\nOR Orden.current_state = \'4\' \r\nOR Orden.current_state = \'5\' \r\nOR Orden.current_state = \'18\'\r\nOR Orden.current_state = \'25\'\r\nOR Orden.current_state = \'26\'   )\r\n      AND Orden.date_add BETWEEN [*START_DATE*] AND [*FINISH_DATE*] ) AS Descuentos\r\nFROM [*PREFIX*]orders AS Orden\r\nINNER JOIN [*PREFIX*]cart AS Carro\r\n      ON Carro.id_cart = Orden.id_cart\r\nINNER JOIN [*PREFIX*]cart_product AS CarroProducto\r\n      ON CarroProducto.id_cart = Carro.id_cart\r\nINNER JOIN [*PREFIX*]product AS Producto\r\n      ON Producto.id_product = CarroProducto.id_product\r\nINNER JOIN [*PREFIX*]product_lang AS ProductoLang\r\n      ON ProductoLang.id_product = Producto.id_product\r\nINNER JOIN [*PREFIX*]manufacturer AS Fabricante\r\n  ON Fabricante.id_manufacturer = Producto.id_manufacturer\r\nINNER JOIN [*PREFIX*]order_detail AS DetalleOrden\r\n      ON DetalleOrden.`product_id` = Producto.`id_product`\r\n      AND DetalleOrden.`id_order` = Orden.`id_order`\r\nWHERE \r\n      Orden.date_add BETWEEN [*START_DATE*] AND [*FINISH_DATE*]\r\n      AND (\r\n       Orden.current_state = \'2\' \r\n  OR Orden.current_state = \'3\'  \r\n  OR Orden.current_state = \'4\' \r\n  OR Orden.current_state = \'5\' \r\n  OR Orden.current_state = \'18\'\r\n  OR Orden.current_state = \'25\'\r\n  OR Orden.current_state = \'26\' )\r\nGROUP BY Fabricante.id_manufacturer\r\nORDER BY Total DESC',1,'2017-04-04 12:10:59','2017-04-07 16:20:50');

